02-active-buttons.code:
import React from 'react';
import { Group } from 'ui-lab-components';

export default function Example() {
  const [activeTab, setActiveTab] = React.useState('overview');

  return (
    <div className="space-y-6">
      {/* Ghost variant with active state - tab-like interface */}
      <div className="space-y-2">
        <label className="text-sm text-foreground-300">Ghost variant (recommended for tabs)</label>
        <Group variant="ghost">
          <Group.Button
            active={activeTab === 'overview'}
            onClick={() => setActiveTab('overview')}
          >
            Overview
          </Group.Button>
          <Group.Button
            active={activeTab === 'details'}
            onClick={() => setActiveTab('details')}
          >
            Details
          </Group.Button>
          <Group.Button
            active={activeTab === 'settings'}
            onClick={() => setActiveTab('settings')}
          >
            Settings
          </Group.Button>
        </Group>
      </div>

      {/* Primary variant with active state */}
      <div className="space-y-2">
        <label className="text-sm text-foreground-300">Primary variant with active</label>
        <Group variant="primary">
          <Group.Button active>Active</Group.Button>
          <Group.Button>Inactive</Group.Button>
          <Group.Button>Inactive</Group.Button>
        </Group>
      </div>

      {/* Outline variant with active state */}
      <div className="space-y-2">
        <label className="text-sm text-foreground-300">Outline variant with active</label>
        <Group variant="outline">
          <Group.Button>Inactive</Group.Button>
          <Group.Button active>Active</Group.Button>
          <Group.Button>Inactive</Group.Button>
        </Group>
      </div>
    </div>
  );
}
