            <ThoughtStep key={step.number} {...step} />
          ))}
        </div>

        <div className="mt-8 p-4 bg-background-800 border border-background-700 rounded-md">
          <p className="text-sm text-foreground-300">
            The chain of thought shows the reasoning process, making it easier to understand how the AI arrived at the conclusion.
          </p>
        </div>
      </div>
    </div>
  );
}

interface ThoughtStepProps {
  number: number;
  title: string;
  description: string;
}

export function ThoughtStep({ number, title, description }: ThoughtStepProps) {
  return (
    <div className="flex gap-4">
      <div className="flex flex-col items-center">
        <div className="w-10 h-10 rounded-full bg-accent-500 text-foreground-50 flex items-center justify-center font-bold text-sm">
          {number}
        </div>
        {number < 4 && <div className="w-1 h-8 bg-accent-500 mt-2" />}
      </div>
      <div className="flex-1 pt-1 pb-4">
        <h3 className="font-semibold text-foreground-50 mb-1">{title}</h3>
        <p className="text-sm text-foreground-400">{description}</p>
      </div>
    </div>
  );
}

"use client"
import React, { useState } from 'react';
import { ExpandableThoughtStep } from './layout/ExpandableThoughtStep';

export function ChainOfThoughtWithDetails() {
  const [expandedSteps, setExpandedSteps] = useState<Set<number>>(new Set([1]));

  const steps = [
    {
      number: 1,
      title: 'Understanding the Request',
      description: 'Parsing and understanding what the user is asking for',
      details: [
        'Identified key requirements from the input',
        'Recognized the problem domain',
        'Determined necessary context and constraints',
      ],
    },
    {
      number: 2,
      title: 'Analyzing Available Approaches',
      description: 'Evaluating different methods to solve the problem',
      details: [
        'Approach A: Direct computation (Fast but limited)',
        'Approach B: Iterative refinement (Balanced)',
        'Approach C: Machine learning (Slow but accurate)',
      ],
    },
    {
      number: 3,
      title: 'Selecting Optimal Strategy',
      description: 'Choosing the best approach based on constraints',
      details: [
        'Evaluated trade-offs between speed and accuracy',
        'Considered resource constraints',
        'Selected Approach B for optimal balance',
      ],
    },
    {
      number: 4,
      title: 'Generating Solution',
      description: 'Implementing the chosen strategy step by step',
      details: [
        'Breaking down into sub-tasks',
        'Validating intermediate results',
        'Optimizing for performance',
      ],
    },
  ];

  const toggleStep = (number: number) => {
    const newExpanded = new Set(expandedSteps);
    if (newExpanded.has(number)) {
      newExpanded.delete(number);
    } else {
      newExpanded.add(number);
    }
    setExpandedSteps(newExpanded);
  };

  return (
    <div className="min-h-screen bg-background-950 p-8">
      <div className="max-w-2xl mx-auto">
        <h1 className="text-3xl font-bold text-foreground-50 mb-2">Detailed AI Reasoning</h1>
        <p className="text-foreground-400 mb-8">Click steps to see detailed reasoning:</p>

        <div className="space-y-3">
          {steps.map((step) => (
            <ExpandableThoughtStep
              key={step.number}
              {...step}
              isExpanded={expandedSteps.has(step.number)}
              onToggle={() => toggleStep(step.number)}
            />
          ))}
        </div>
      </div>
    </div>
  );
}

interface ExpandableThoughtStepProps {
  number: number;
  title: string;
  description: string;
  details: string[];
  isExpanded: boolean;
  onToggle: () => void;
}

export function ExpandableThoughtStep({
  number,
  title,
  description,
  details,
  isExpanded,
  onToggle,
}: ExpandableThoughtStepProps) {
  return (
    <div className="bg-background-800 border border-background-700 rounded-md overflow-hidden">
      <button
        onClick={onToggle}
        className="w-full flex items-start gap-4 p-4 hover:bg-background-700/50 transition-colors text-left"
      >
        <div className="w-10 h-10 rounded-full bg-accent-500 text-foreground-50 flex items-center justify-center font-bold text-sm flex-shrink-0">
          {number}
        </div>
        <div className="flex-1 pt-1">
          <h3 className="font-semibold text-foreground-50 mb-1">{title}</h3>
          <p className="text-sm text-foreground-400">{description}</p>
        </div>
        <span className="text-foreground-500 flex-shrink-0 mt-1">{isExpanded ? '▼' : '▶'}</span>
      </button>

      {isExpanded && (
        <div className="border-t border-background-700 bg-background-700/30 p-4">
          <ul className="space-y-2">
            {details.map((detail, index) => (
              <li key={index} className="flex gap-3 text-sm text-foreground-300">
                <span className="text-accent-400 flex-shrink-0">•</span>
                <span>{detail}</span>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
