"use client"
import React, { useState } from 'react';
import { ChatContainer } from './layout/ChatContainer';
import { ChatMessage } from './layout/ChatMessage';
import { ChatInput } from './layout/ChatInput';

interface Message {
  id: string;
  content: string;
  sender: 'ai' | 'user';
}

export function BasicChat() {
  const [messages, setMessages] = useState<Message[]>([
    { id: '1', content: 'Hello! How can I assist you today?', sender: 'ai' },
    { id: '2', content: 'I need help with my project', sender: 'user' },
    { id: '3', content: 'Sure! Tell me more about what you need help with.', sender: 'ai' },
  ]);

  const handleSendMessage = (content: string) => {
    const userMessage = {
      id: String(messages.length + 1),
      content,
      sender: 'user' as const,
    };
    setMessages([...messages, userMessage]);

    setTimeout(() => {
      const aiMessage = {
        id: String(messages.length + 2),
        content: 'That sounds interesting! I can help you with that.',
        sender: 'ai' as const,
      };
      setMessages((prev) => [...prev, aiMessage]);
    }, 500);
  };

  return (
    <div className="min-h-screen bg-background-950 p-8">
      <div className="max-w-2xl mx-auto h-96">
        <ChatContainer>
          <div className="flex-1 overflow-y-auto space-y-4 p-4">
            {messages.map((message) => (
              <ChatMessage key={message.id} message={message.content} sender={message.sender} />
            ))}
          </div>
          <div className="border-t border-background-700 p-4">
            <ChatInput onSendMessage={handleSendMessage} />
          </div>
        </ChatContainer>
      </div>
    </div>
  );
}

import React from 'react';

interface ChatContainerProps {
  children: React.ReactNode;
}

export function ChatContainer({ children }: ChatContainerProps) {
  return (
    <div className="flex flex-col h-full bg-background-800 border border-background-700 rounded-md overflow-hidden">
      {children}
    </div>
  );
}

import React, { useState } from 'react';

interface ChatInputProps {
  onSendMessage: (message: string) => void;
}

export function ChatInput({ onSendMessage }: ChatInputProps) {
  const [input, setInput] = useState('');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (input.trim()) {
      onSendMessage(input);
      setInput('');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="flex gap-2">
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="Type a message..."
        className="flex-1 bg-background-700 border border-background-600 rounded px-3 py-2 text-sm text-foreground-50 placeholder-foreground-400 focus:outline-none focus:border-accent-500"
      />
      <button
        type="submit"
        className="px-4 py-2 bg-accent-500 text-foreground-50 rounded font-medium hover:bg-accent-600 transition-colors text-sm"
      >
        Send
      </button>
    </form>
  );
}

interface ChatMessageProps {
  message: string;
  sender: 'user' | 'ai';
}

export function ChatMessage({ message, sender }: ChatMessageProps) {
  return (
    <div className={`flex ${sender === 'user' ? 'justify-end' : 'justify-start'}`}>
      <div
        className={`max-w-xs px-4 py-2 rounded-lg text-sm ${
          sender === 'user'
            ? 'bg-accent-500 text-foreground-50'
            : 'bg-background-700 text-foreground-300'
        }`}
      >
        {message}
      </div>
    </div>
  );
}

