"use client";

import { Sidebar } from './layout/Sidebar';

export function BasicSidebar() {
  return (
    <div className="flex min-h-screen bg-background-950">
      <Sidebar activeItem="home" />

      <main className="flex-1">
        {/* Content */}
      </main>
    </div>
  );
}

import { useState } from 'react';
import {
  FiHome,
  FiUser,
  FiSettings,
  FiLogOut,
  FiChevronLeft,
  FiChevronRight,
  FiBox,
  FiPieChart,
  FiMessageSquare,
  FiBell,
  FiHelpCircle,
  FiBriefcase
} from 'react-icons/fi';
import { Divider } from 'ui-lab-components';

interface SidebarProps {
  activeItem?: string;
}

export function Sidebar({ activeItem = 'home' }: SidebarProps) {
  const [isCollapsed, setIsCollapsed] = useState(false);

  const mainNavItems = [
    { id: 'home', label: 'Dashboard', icon: FiHome },
    { id: 'projects', label: 'Projects', icon: FiBriefcase },
    { id: 'analytics', label: 'Analytics', icon: FiPieChart },
    { id: 'messages', label: 'Messages', icon: FiMessageSquare },
  ];

  const secondaryNavItems = [
    { id: 'profile', label: 'Profile', icon: FiUser },
    { id: 'notifications', label: 'Notifications', icon: FiBell },
    { id: 'settings', label: 'Settings', icon: FiSettings },
  ];

  const bottomNavItems = [
    { id: 'help', label: 'Help & Support', icon: FiHelpCircle },
  ];

  return (
    <aside
      className={`
        ${isCollapsed ? 'w-24' : 'w-80'} 
        bg-background-900 border-r border-background-800 
        flex flex-col transition-all duration-300 ease-in-out
        h-screen sticky top-0 shadow-xl
      `}
    >
      {/* Sidebar Header */}
      <div className="h-20 flex items-center justify-between px-6 shrink-0">
        {!isCollapsed && (
          <div className="flex items-center gap-4 overflow-hidden whitespace-nowrap animate-in fade-in duration-300">
            <div className="w-10 h-10 bg-accent-600 rounded-xl flex items-center justify-center shrink-0 text-white shadow-md">
              <FiBox size={22} />
            </div>
            <div className="flex flex-col">
              <span className="text-md font-bold text-foreground-50 leading-none">UI Lab</span>
              <span className="text-sm text-foreground-400 mt-1 font-medium">Enterprise</span>
            </div>
          </div>
        )}

        <button
          onClick={() => setIsCollapsed(!isCollapsed)}
          className={`
            p-2.5 rounded-xl text-foreground-400 hover:bg-background-800 hover:text-foreground-50 
            transition-all focus:outline-none focus:ring-2 focus:ring-accent-500/30
            ${isCollapsed ? 'mx-auto' : ''}
          `}
          aria-label={isCollapsed ? "Expand sidebar" : "Collapse sidebar"}
        >
          {isCollapsed ? <FiChevronRight size={22} /> : <FiChevronLeft size={22} />}
        </button>
      </div>

      <div className="px-6">
        <Divider className="bg-background-800" />
      </div>

      {/* Navigation Items */}
      <nav className="flex-1 px-4 py-6 space-y-8 overflow-y-auto overflow-x-hidden custom-scrollbar">
        {/* Main Section */}
        <div className="space-y-2">
          {!isCollapsed && (
            <p className="px-3 text-xs font-bold text-foreground-500 uppercase tracking-wider mb-3 animate-in fade-in duration-300">
              Menu
            </p>
          )}
          {mainNavItems.map((item) => (
            <NavItem key={item.id} item={item} isActive={activeItem === item.id} isCollapsed={isCollapsed} />
          ))}
        </div>

        {/* Secondary Section */}
        <div className="space-y-2">
          {!isCollapsed && (
            <p className="px-3 text-xs font-bold text-foreground-500 uppercase tracking-wider mb-3 animate-in fade-in duration-300">
              General
            </p>
          )}
          {secondaryNavItems.map((item) => (
            <NavItem key={item.id} item={item} isActive={activeItem === item.id} isCollapsed={isCollapsed} />
          ))}
        </div>
      </nav>

      {/* Footer */}
      <div className="p-4 shrink-0 space-y-2 bg-background-900 z-10">
        <Divider className="mb-4 bg-background-800" />

        {bottomNavItems.map((item) => (
          <NavItem key={item.id} item={item} isActive={activeItem === item.id} isCollapsed={isCollapsed} />
        ))}

        <button
          className={`
            w-full flex items-center gap-4 px-3 py-3 
            text-foreground-400 hover:text-red-400 hover:bg-red-500/10 
            rounded-xl transition-all duration-200 group
            ${isCollapsed ? 'justify-center' : ''}
          `}
          title={isCollapsed ? "Logout" : undefined}
        >
          <FiLogOut size={22} className="shrink-0 transition-colors" />
          {!isCollapsed && (
            <span className="text-base font-medium whitespace-nowrap overflow-hidden animate-in fade-in slide-in-from-left-2 duration-200">
              Logout
            </span>
          )}
        </button>

        {!isCollapsed && (
          <div className="px-3 pt-2 text-center animate-in fade-in duration-500">
            <p className="text-xs text-foreground-600 font-medium">v1.2.0</p>
          </div>
        )}
      </div>
    </aside>
  );
}

function NavItem({ item, isActive, isCollapsed }: { item: any, isActive: boolean, isCollapsed: boolean }) {
  const Icon = item.icon;
  return (
    <a
      href="#"
      className={`
            flex items-center gap-4 px-3 py-3 rounded-xl transition-all duration-200 group relative
            ${isActive
          ? 'bg-accent-500/10 text-accent-500 shadow-sm'
          : 'text-foreground-400 hover:text-foreground-50 hover:bg-background-800'}
            ${isCollapsed ? 'justify-center' : ''}
          `}
      title={isCollapsed ? item.label : undefined}
    >
      <Icon
        size={22}
        className={`shrink-0 transition-transform duration-200 ${isActive ? '' : 'group-hover:scale-110'}`}
      />

      {!isCollapsed && (
        <span className="text-md font-medium whitespace-nowrap overflow-hidden animate-in fade-in slide-in-from-left-2 duration-200">
          {item.label}
        </span>
      )}
    </a>
  )
}

import React from 'react';
import { Sidebar } from './layout/Sidebar';

export function SidebarWithContent() {
  return (
    <div className="flex min-h-screen bg-background-950">
      <Sidebar activeItem="dashboard" />

      <main className="flex-1 flex flex-col">
        {/* Top Bar */}
        <div className="bg-background-800 border-b border-background-700 px-8 py-4">
          <h1 className="text-2xl font-bold text-foreground-50">Dashboard</h1>
        </div>

        {/* Main Content */}
        <div className="flex-1 overflow-auto">
          <div className="max-w-6xl mx-auto px-8 py-8">
            <div className="space-y-6">
              {/* Stats Grid */}
              <div className="grid grid-cols-3 gap-4">
                {[1, 2, 3].map((i) => (
                  <div key={i} className="bg-background-800 rounded-lg border border-background-700 p-6">
                    <div className="text-sm text-foreground-400 mb-2">Stat {i}</div>
                    <div className="text-3xl font-bold text-accent-500 mb-2">1,234</div>
                    <div className="text-xs text-foreground-500">+12% from last month</div>
                  </div>
                ))}
              </div>

              {/* Charts */}
              <div className="grid grid-cols-2 gap-4">
                {[1, 2].map((i) => (
                  <div key={i} className="bg-background-800 rounded-lg border border-background-700 p-6">
                    <div className="text-sm font-medium text-foreground-50 mb-4">Chart {i}</div>
                    <div className="h-40 bg-background-700 rounded flex items-end justify-between gap-2 p-4">
                      {[60, 75, 50, 90, 65].map((height, idx) => (
                        <div
                          key={idx}
                          className="flex-1 bg-accent-500 rounded-sm"
                          style={{ height: `${height}%` }}
                        />
                      ))}
                    </div>
                  </div>
                ))}
              </div>

              {/* Table */}
              <div className="bg-background-800 rounded-lg border border-background-700 p-6">
                <div className="text-sm font-medium text-foreground-50 mb-4">Recent Activity</div>
                <div className="space-y-3">
                  {[1, 2, 3, 4].map((i) => (
                    <div key={i} className="flex items-center justify-between p-4 bg-background-700 rounded">
                      <div className="flex-1">
                        <div className="h-3 bg-background-600 rounded w-1/4 mb-2" />
                        <div className="h-2 bg-background-600 rounded w-1/3" />
                      </div>
                      <div className="h-2 bg-background-600 rounded w-16 ml-4" />
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}

import React from 'react';

interface SidebarProps {
  activeItem?: string;
}

export function Sidebar({ activeItem = 'dashboard' }: SidebarProps) {
  const menuItems = [
    { id: 'dashboard', label: 'Dashboard' },
    { id: 'products', label: 'Products' },
    { id: 'orders', label: 'Orders' },
    { id: 'home', label: 'Home' },
    { id: 'settings', label: 'Settings' },
  ];

  return (
    <aside className="w-64 bg-background-800 border-r border-background-700 flex flex-col">
      {/* Sidebar Header */}
      <div className="p-6 border-b border-background-700">
        <div className="flex items-center gap-3">
          <div className="w-8 h-8 bg-accent-500 rounded-lg" />
          <span className="text-lg font-semibold text-foreground-50">Dashboard</span>
        </div>
      </div>

      {/* Navigation Items */}
      <nav className="flex-1 p-4 space-y-2 overflow-y-auto">
        {menuItems.map((item) => {
          const isActive = activeItem === item.id;
          return (
            <a
              key={item.id}
              href="#"
              className={`flex items-center gap-3 px-4 py-3 rounded-lg transition-colors ${
                isActive
                  ? 'bg-accent-500 text-foreground-50'
                  : 'text-foreground-400 hover:text-foreground-300 hover:bg-background-700'
              }`}
            >
              <div className="w-5 h-5 bg-background-700 rounded flex-shrink-0" />
              <span className="text-sm font-medium">{item.label}</span>
            </a>
          );
        })}
      </nav>

      {/* User Section */}
      <div className="p-4 border-t border-background-700 space-y-3">
        <div className="flex items-center gap-3 px-4 py-3 rounded-lg bg-background-700">
          <div className="w-8 h-8 bg-accent-500 rounded-full flex-shrink-0" />
          <div className="flex-1 min-w-0">
            <div className="text-sm font-medium text-foreground-50 truncate">John Doe</div>
            <div className="text-xs text-foreground-400 truncate">john@example.com</div>
          </div>
        </div>
      </div>
    </aside>
  );
}
