import { Badge } from 'ui-lab-components'

const statusVariant = {
  active: 'success',
  beta: 'warning',
  deprecated: 'danger',
  draft: 'default',
} as const

function BadgeRowMixed({ items }) {
  return (
    <div className="flex flex-wrap gap-1.5">
      {items.map((item) => (
        <Badge
          key={item.label}
          size="sm"
          variant={statusVariant[item.status] ?? 'default'}
        >
          {item.label}
        </Badge>
      ))}
    </div>
  )
}
