import { Progress } from 'ui-lab-components'

function ProgressMetricStacked({ label, value, max = 100 }) {
  const pct = Math.round((value / max) * 100)
  return (
    <div className="flex flex-col gap-1.5">
      <div className="flex items-center justify-between">
        <span className="text-xs font-medium text-foreground-300">{label}</span>
        <span className="text-xs text-foreground-400 tabular-nums">{pct}%</span>
      </div>
      <Progress value={value} maxValue={max} />
    </div>
  )
}

import { Progress } from 'ui-lab-components'

function ProgressMetricColored({ label, value, max = 100 }) {
  const pct = Math.round((value / max) * 100)
  const isHigh = pct >= 80
  return (
    <div className="flex flex-col gap-1.5">
      <div className="flex items-center justify-between">
        <span className="text-xs font-medium text-foreground-300">{label}</span>
        <span className={`text-xs tabular-nums ${isHigh ? 'text-foreground-200' : 'text-foreground-400'}`}>
          {pct}%
        </span>
      </div>
      <Progress
        value={value}
        maxValue={max}
        className={isHigh ? 'text-foreground-300' : undefined}
      />
    </div>
  )
}
