import { Button } from 'ui-lab-components'

function EmptyState({ icon, title, description, actionLabel, onAction }) {
  return (
    <div className="flex flex-col items-center justify-center gap-4 py-12 text-center">
      {icon && (
        <div className="w-12 h-12 flex items-center justify-center rounded-full bg-background-800 text-foreground-400">
          {icon}
        </div>
      )}
      <div className="max-w-xs space-y-1">
        <p className="text-sm font-medium text-foreground-200">{title}</p>
        {description && (
          <p className="text-sm text-foreground-400">{description}</p>
        )}
      </div>
      {actionLabel && (
        <Button size="sm" onPress={onAction}>{actionLabel}</Button>
      )}
    </div>
  )
}

