code:
import { Select, Label } from 'ui-lab-components'

const options = [
  { id: 'system', label: 'System' },
  { id: 'light', label: 'Light' },
  { id: 'dark', label: 'Dark' },
]

function SelectRow({ id, label, description, defaultValue }) {
  return (
    <div className="flex items-center justify-between gap-4 py-3 border-b border-background-800">
      <div className="flex-1 min-w-0">
        <Label htmlFor={id} className="text-sm font-medium text-foreground-200">
          {label}
        </Label>
        {description && (
          <p className="text-xs text-foreground-400 mt-0.5">{description}</p>
        )}
      </div>
      <Select defaultSelectedKey={defaultValue}>
        {options.map((opt) => (
          <Select.Option key={opt.id} id={opt.id}>{opt.label}</Select.Option>
        ))}
      </Select>
    </div>
  )
}

