  { id: 'fr', label: 'French' },
  { id: 'de', label: 'German' },
]

function SelectRowWithIcon({ id, label, description, icon, defaultValue }) {
  return (
    <div className="flex items-center gap-4 py-3 border-b border-background-800">
      <div className="w-8 h-8 flex items-center justify-center rounded-md bg-background-800 text-foreground-400 flex-shrink-0">
