      <div className="flex-1 min-w-0">
        <Label htmlFor={id} className="text-sm font-medium text-foreground-200">
          {label}
        </Label>
        {description && (
          <p className="text-xs text-foreground-400 mt-0.5">{description}</p>
        )}
      </div>
      <Select defaultSelectedKey={defaultValue}>
        {languages.map((lang) => (
          <Select.Option key={lang.id} id={lang.id}>{lang.label}</Select.Option>
        ))}
      </Select>
