import { Button } from 'ui-lab-components'

function TabContentHeader({ title, description, actionLabel, onAction }) {
  return (
    <div className="flex items-start justify-between gap-4 pb-4 border-b border-background-800">
      <div>
        <h3 className="text-sm font-semibold text-foreground-100">{title}</h3>
        {description && (
          <p className="text-sm text-foreground-400 mt-0.5">{description}</p>
        )}
      </div>
      {actionLabel && (
        <Button size="sm" variant="secondary" onPress={onAction}>
          {actionLabel}
        </Button>
      )}
    </div>
  )
}

