function TabContentHeaderMinimal({ title }) {
  return (
    <div className="pb-3 border-b border-background-800">
      <h3 className="text-sm font-semibold text-foreground-100">{title}</h3>
    </div>
  )
}

import { Button, Badge } from 'ui-lab-components'

function TabContentHeaderWithBadge({ title, count, actionLabel, onAction }) {
  return (
    <div className="flex items-center justify-between gap-4 pb-4 border-b border-background-800">
      <div className="flex items-center gap-2">
        <h3 className="text-sm font-semibold text-foreground-100">{title}</h3>
        <Badge size="sm" variant="secondary">{count}</Badge>
      </div>
      {actionLabel && (
        <Button size="sm" variant="secondary" onPress={onAction}>
          {actionLabel}
        </Button>
      )}
    </div>
  )
}
