function MediaObject({ icon, title, description }) {
  return (
    <div className="flex items-start gap-3">
      <div className="w-10 h-10 flex items-center justify-center rounded-lg bg-background-800 text-foreground-300 flex-shrink-0">
        {icon}
      </div>
      <div className="min-w-0">
        <p className="text-sm font-medium text-foreground-200">{title}</p>
        <p className="text-sm text-foreground-400">{description}</p>
      </div>
    </div>
  )
}

